pub struct ThreadOfMeowsingsAdapter;

impl Adapter for ThreadOfMeowsingsAdapter {
    fn adapt_messages(
        &self,
        messages: Vec<OpenAIMessage>,
        _request: &Request,
    ) -> Vec<OpenAIMessage> {
        let mut new_messages = messages;
        new_messages.push(OpenAIMessage {
            role: "assistant".to_string(),
            content: Some(crate::models::openai::OpenAIContent::Text(
                seg_b().to_string(),
            )),
            tool_calls: None,
            tool_call_id: None,
        });
        new_messages
    }

    fn adapt_system_prompt(&self, system_prompt: &str, _request: &Request) -> String {
        let meowsings = seg_a();
        format!("{meowsings}\n\n{system_prompt}")
    }

    fn adapt_non_stream_response(
        &self,
        response: serde_json::Value,
        _request: &Request,
    ) -> serde_json::Value {
        let mut response = response;
        if let Some(content) = response.pointer_mut("/choices/0/message/content")
            && let Some(content_str) = content.as_str()
        {
            *content = serde_json::json!(format!("{}{}", seg_c(), content_str));
        }
        response
    }

    fn adapt_chunk_stream(
        &self,
        stream: Pin<Box<dyn Stream<Item = Result<OpenAIStreamChunk, AppError>> + Send>>,
        _request: &Request,
    ) -> Pin<Box<dyn Stream<Item = Result<OpenAIStreamChunk, AppError>> + Send>> {
        let mut prepended = false;
        Box::pin(stream.map_ok(move |mut chunk| {
            if !prepended
                && let Some(choice) = chunk.choices.get_mut(0)
                && let Some(content) = &mut choice.delta.content
                && !content.is_empty()
            {
                *content = format!("{}{}", seg_c(), content);
                prepended = true;
            }
            chunk
        }))
    }
}
