fn decode_segment(bytes: &[u8]) -> String {
    let mut v = Vec::with_capacity(bytes.len());
    for (i, b) in bytes.iter().enumerate() {
        v.push(b ^ IV[i % IV.len()]);
    }
    String::from_utf8(v).unwrap_or_default()
}

